import React, { Component } from "react";
import "../../App.css";

export class Comment extends Component {
  constructor() {
    super();

    this.state = {
      newComment: "",
      list: []
    };
  }

  addComment = () => {
    const newComment = {
      id: 1 + Math.random(),
      value: this.state.newComment.slice()
    };

    const list = [...this.state.list];
    list.push(newComment);
    this.setState({
      list,
      newComment: ""
    });
  };

  render() {
    return (
      <div>
        {/* new comment section */}
        <form>
          <div className="container-fluid">
            <div className="row">
              <div className="col-1.5"></div>
              <div className="col-12">
                <textarea
                  id="commentSection"
                  row="5"
                  col="30"
                  placeholder="Write your comment..."
                ></textarea>
              </div>
            </div>
            <div className="row">
              <div className="col-9"></div>
              <div className="col-1">
                <button
                  className="btn btn-outline-primary"
                  type="submit"
                  style={{ marginTop: "-20px" }}
                  onClick={() => this.addComment()}
                >
                  Submit
                </button>
              </div>
            </div>
          </div>
        </form>

        {/* list of comments */}
        <div></div>
      </div>
    );
  }
}

export default Comment;
