import React, { Component } from "react";
import Sidebar from "./Sidebar";
import styled from "styled-components";

const Hamburger = ({ onClick, openSidebar }) => {
  return (
    <HamburgerWrapper>
      <div />
      <div />
      <div />
      <Sidebar sidebarOpen />
    </HamburgerWrapper>
  );
};

const HamburgerWrapper = styled.div`
  position: relative;
  top: 0.5rem;
  left: 1rem;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  width: 2rem;
  height: 2rem;
  background: transparent;
  border: none;
  cursor: pointer;
  padding: 0
  z-index: 10;

  &:focus {
    outline: none;
  }

  div {
    width: 2rem;
    height: 0.25rem;
    background: black;
    border-radius: 10px;
    transition: all 0.3s linear;
    position: relative;
    transform-origin: 1px;
  }
`;

export default Hamburger;
